actionConfiguration.body:
do 
$$
declare
	test_nam varchar;


begin
	if {{school.selectedOptionValue}} = 11 then 	
	begin
		raise notice 'School Test LPS % %' ,sec,tes_no::integer;
		test_nam= format('School Test LPS %s %s',{{section.selectedOptionLabel}} , {{new_test_num.text}});
	end 

	elsif {{school.selectedOptionValue}} = 12 then
	begin
		raise notice 'School Test JPS % %' ,sec,tes_no::integer;
	   test_nam= format('School Test JPS %s %s',{{section.selectedOptionLabel}} , {{new_test_num.text}});
  	end

	 elsif {{school.selectedOptionValue}} = 13 then
  	begin
		raise notice 'School Test AKV % %' ,sec,tes_no::integer;
		test_nam= format('School Test AKV %s %s',{{section.selectedOptionLabel}} , {{new_test_num.text}});

  	end

	 elsif {{school.selectedOptionValue}} = 14 then
 	begin
		raise notice 'School Test MVM % %' ,sec,tes_no::integer;
		test_nam= format('School Test MVM %s %s',{{section.selectedOptionLabel}} , {{new_test_num.text}});
	end
	end if;
		end;
$$
